import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, TextInput, Modal, Image } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';


const ProductDetailScreen = ({ route }) => {
  const { product } = route.params;
  const [isQuantityModalVisible, setQuantityModalVisible] = useState(false);
  const [quantity, setQuantity] = useState(1);

  const showQuantityModal = () => {
    setQuantityModalVisible(true);
  };

  const hideQuantityModal = () => {
    setQuantityModalVisible(false);
  };

  const handleAddToCart = async () => {
    try {
      // Use AsyncStorage from react-native
      const storedUserId = await AsyncStorage.getItem('user_id');
      if (storedUserId) {
        const user_id = parseInt(storedUserId, 10); // Convert to integer
        const product_id = product.product_id;
        const total_price = product.Price * quantity;

        const formData = new FormData();
        formData.append('user_id', user_id);
        formData.append('product_id', product_id);
        formData.append('quantity', quantity);
        formData.append('total_price', total_price);

        const apiUrl = 'http://192.168.1.18/MyReactNative1/addToCart.php';

        fetch(apiUrl, {
          method: 'POST',
          body: formData,
        })
          .then((response) => response.text())
          .then((text) => {
            console.log('Server response:', text);
            // Handle the response from the server as needed
            setQuantityModalVisible(false);
          })
          .catch((error) => {
            console.error('Error:', error);
          });
      } else {
        console.error('User ID not found in storage.');
      }
    } catch (error) {
      console.error('AsyncStorage error:', error);
    }
  };


  const handleCheckout = () => {
    console.log('Checkout initiated for:', product.ProductName);
    // Implement your logic for the checkout process
  };

  return (
    <View style={styles.container}>
      <View style={styles.productInfo}>
        {/* Display Image */}
        {product.Images && product.Images.length > 0 && (
          <Image
            source={{ uri: product.Images[0].ImageUrl }}
            style={styles.productImage}
          />
        )}

        <Text style={styles.productName}>{product.ProductName}</Text>
        <Text style={styles.productPrice}>{`$${product.Price}`}</Text>
        <Text style={styles.productStock}>{`Stock: ${product.Stocks}`}</Text>
      </View>

      <View style={styles.space} />

      {/* Add to Cart button */}
      <TouchableOpacity
        style={styles.button}
        onPress={showQuantityModal}
      >
        <Text style={styles.buttonText}>Add to Cart</Text>
      </TouchableOpacity>

      <View style={styles.space} />

      {/* Checkout button */}
      <TouchableOpacity
        style={[styles.button, { backgroundColor: 'orange' }]}
        onPress={handleCheckout}
      >
        <Text style={styles.buttonText}>Checkout</Text>
      </TouchableOpacity>

      {/* Quantity Input Modal */}
      <Modal
        transparent
        animationType="slide"
        visible={isQuantityModalVisible}
        onRequestClose={hideQuantityModal}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>{product.ProductName}</Text>
            <Text style={styles.greenText}>{`Price: $${product.Price}`}</Text>
            <Text style={styles.greenText}>{`Stocks: ${product.Stocks}`}</Text>

            <View style={styles.quantityContainer}>
              <Text>Quantity</Text>
              <View style={styles.quantityInputContainer}>
                <TouchableOpacity
                  style={[styles.quantityButton, { backgroundColor: 'red' }]}
                  onPress={() => setQuantity(Math.max(1, quantity - 1))}
                >
                  <Text style={styles.buttonText}>-</Text>
                </TouchableOpacity>

                <TextInput
                  style={[
                    styles.quantityInput,
                    { textAlign: 'center', flex: 0, marginLeft: 8, marginRight: 8, marginBottom: 3 },
                  ]}
                  keyboardType="numeric"
                  value={quantity.toString()}
                  onChangeText={(text) => setQuantity(text)}
                />

                <TouchableOpacity
                  style={styles.quantityButton}
                  onPress={() => setQuantity(Math.min(product.Stocks, quantity + 1))}
                >
                  <Text style={styles.buttonText}>+</Text>
                </TouchableOpacity>
              </View>
            </View>

            <TouchableOpacity
              style={[styles.modalButton, { backgroundColor: 'blue' }]}
              onPress={handleAddToCart}
            >
              <Text style={styles.buttonText}>Add to Cart</Text>
            </TouchableOpacity>

            <TouchableOpacity
              style={[styles.modalButton, { backgroundColor: 'red' }]}
              onPress={hideQuantityModal}
            >
              <Text style={styles.buttonText}>Cancel</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'flex-end',
  },
  productInfo: {
    marginBottom: 16,
  },
  productImage: {
    width: '100%',
    height: 500, // Adjust the height as needed
    resizeMode: 'cover', // or 'contain' depending on your preference
    borderRadius: 8,
    marginBottom: 8,
  },
  productName: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  productPrice: {
    fontSize: 18,
    color: 'green',
    marginTop: 8,
  },
  productStock: {
    fontSize: 16,
    color: 'blue',
    marginTop: 8,
  },
  button: {
    padding: 16,
    borderRadius: 8,
    backgroundColor: 'blue',
    alignItems: 'center',
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  space: {
    height: 16,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'flex-end',
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 16,
    borderTopLeftRadius: 16,
    borderTopRightRadius: 16,
  },
  quantityInput: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 16,
    paddingHorizontal: 8,
  },
  modalButton: {
    padding: 16,
    borderRadius: 8,
    backgroundColor: 'blue',
    alignItems: 'center',
    marginBottom: 8,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  greenText: {
    color: 'green',
  },
  quantityContainer: {
    marginBottom: 16,
  },
  quantityInputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginRight: 'auto', // This will push the container to the right
  },
  quantityButton: {
    backgroundColor: 'blue',
    borderRadius: 4,
    padding: 8,
  },
});

export default ProductDetailScreen;
