// RegistrationScreen.js
import React, { useState } from 'react';
import { View, StyleSheet, TextInput, TouchableOpacity } from 'react-native';
import { Text, TextInput as PaperTextInput } from 'react-native-paper';

const RegistrationScreen = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = () => {
    // Implement your registration logic here
    console.log('Registering...');
    console.log('Username:', username);
    console.log('Email:', email);
    console.log('Password:', password);
    // Add your registration logic (e.g., API call) here
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Register</Text>

      <PaperTextInput
        style={styles.input}
        label="Username"
        value={username}
        onChangeText={(text) => setUsername(text)}
        placeholder="Enter your username"
      />

      <PaperTextInput
        style={styles.input}
        label="Email"
        value={email}
        onChangeText={(text) => setEmail(text)}
        keyboardType="email-address"
        placeholder="Enter your email"
      />

      <PaperTextInput
        style={styles.input}
        label="Password"
        value={password}
        onChangeText={(text) => setPassword(text)}
        secureTextEntry
        placeholder="Enter your password"
      />

      <TouchableOpacity style={styles.registerButton} onPress={handleRegister}>
        <Text style={styles.buttonText}>Register</Text>
      </TouchableOpacity>

      <View style={styles.bottomContainer}>
        <Text>Already have an account?</Text>
        {/* Add navigation to your login screen here */}
        <TouchableOpacity onPress={() => console.log('Navigate to login screen')}>
          <Text style={styles.loginText}>Login</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 16,
  },
  title: {
    fontSize: 32,
    marginBottom: 100,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: 12,
    borderWidth: 1,
    borderRadius: 5,
    paddingHorizontal: 10,
  },
  registerButton: {
    backgroundColor: '#3498db',
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
  },
  buttonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  bottomContainer: {
    marginTop: 20,
    alignItems: 'center',
  },
  loginText: {
    color: '#3498db',
    marginTop: 10,
  },
});

export default RegistrationScreen;
