import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Modal,
  TextInput,
} from 'react-native';
// ... (your imports)

const RestaurantPOS = () => {
  const [cart, setCart] = useState([]);
  const [items, setItems] = useState([]);
  const [isModalVisible, setModalVisible] = useState(false);
  const [newItemName, setNewItemName] = useState('');
  const [newItemPrice, setNewItemPrice] = useState('');

  const fetchData = async () => {
    try {
      const response = await fetch('http://127.0.0.1/api.php'); // Replace with your actual PHP script URL
      const data = await response.json();
      console.log('Fetched Data:', data);
      setItems(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const addToCart = (itemId) => {
    const selectedItem = items.find((item) => item.id === itemId);
    if (selectedItem) {
      const updatedCart = [...cart, selectedItem];
      setCart(updatedCart);
    }
  };

  const removeFromCart = (itemId) => {
    const updatedCart = cart.filter((item) => item.id !== itemId);
    setCart(updatedCart);
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price;
    });
    return total.toFixed(2);
  };

  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  const addNewItem = () => {
    // Validate input
    if (newItemName.trim() === '' || isNaN(newItemPrice)) {
      // Handle validation error, maybe show an alert
      return;
    }

    // Create a new item object
    const newItem = {
      id: items.length + 1, // You should use a better method to generate unique IDs
      name: newItemName,
      price: parseFloat(newItemPrice),
    };

    // Update the items list
    setItems([...items, newItem]);

    // Close the modal
    toggleModal();
  };

  return (
    <View style={styles.container}>
      {/* ... (your existing code) */}

      {/* Modal for adding new menu item */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={isModalVisible}
        onRequestClose={toggleModal}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalHeader}>Add New Menu Item</Text>
            <TextInput
              style={styles.input}
              placeholder="Menu Item"
              onChangeText={(text) => setNewItemName(text)}
            />
            <TextInput
              style={styles.input}
              placeholder="Price"
              onChangeText={(text) => setNewItemPrice(text)}
              keyboardType="numeric"
            />
            <Button title="Add" onPress={addNewItem} />
            <Button title="Cancel" onPress={toggleModal} />
          </View>
        </View>
      </Modal>

      {/* Button to open the modal */}
      <TouchableOpacity style={styles.addButton} onPress={toggleModal}>
        <Text style={styles.addButtonText}>+</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  // ... (your existing styles)

  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
    elevation: 5,
  },
  modalHeader: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  addButton: {
    position: 'absolute',
    bottom: 20,
    right: 20,
    backgroundColor: 'green',
    borderRadius: 50,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    elevation: 5,
  },
  addButtonText: {
    color: 'white',
    fontSize: 24,
  },
});

export default RestaurantPOS;
