import React from 'react';
import { View, StyleSheet, ImageBackground } from 'react-native';
import { Title, Button } from 'react-native-paper';

const GetStartedScreen = ({ navigation }) => {
  const handleGetStarted = () => {
    // Handle the action when the user clicks "Get Started"
    // For example, navigate to the next screen
    navigation.navigate('Register'); // Replace 'Home' with the actual home screen name
  };

  return (
    <ImageBackground
    source={require('../image/getstarted.jpg')}
    style={styles.backgroundImage}
  >
    <View style={styles.container}>
      <Title style={styles.title}>Welcome to Your App</Title>
      <Button
        mode="contained"
        style={styles.button}
        onPress={handleGetStarted}
      >
        Get Started
      </Button>
    </View>
  </ImageBackground>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    marginBottom: 20,
    color: 'white',
  },
  button: {
    marginTop: 16,
    backgroundColor: 'blue', // Customize button color
  },
});

export default GetStartedScreen;
