import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';

const PointOfSale = () => {
  const [cart, setCart] = useState([]);
  const items = [
    { name: 'Item 1', price: 10, quantity: 0 },
    { name: 'Item 2', price: 15, quantity: 0 },
    { name: 'Item 3', price: 20, quantity: 0 },
  ];

  const addToCart = (itemIndex, quantity) => {
    const updatedCart = [...cart];
    updatedCart[itemIndex].quantity = quantity;
    setCart(updatedCart);
  };

  const incrementQuantity = (itemIndex) => {
    const updatedCart = [...cart];
    updatedCart[itemIndex].quantity += 1;
    setCart(updatedCart);
  };

  const decrementQuantity = (itemIndex) => {
    const updatedCart = [...cart];
    if (updatedCart[itemIndex].quantity > 0) {
      updatedCart[itemIndex].quantity -= 1;
      setCart(updatedCart);
    }
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price * item.quantity;
    });
    return total;
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Point of Sale</Text>
      {items.map((item, index) => (
        <View key={index} style={styles.itemContainer}>
          <Text>{item.name} - ${item.price}</Text>
          <View style={styles.quantityContainer}>
            <Button title="-" onPress={() => decrementQuantity(index)} />
            <Text>{cart[index]?.quantity || 0}</Text>
            <Button title="+" onPress={() => incrementQuantity(index)} />
          </View>
        </View>
      ))}
      <Button title="Calculate Total" onPress={() => alert(`Total Price: $${calculateTotal()}`)} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  itemContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  quantityContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
});

export default PointOfSale;
