import React, { useState } from 'react';
import { View, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { Text, TextInput as PaperTextInput, Button } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import axios from 'axios';

const RegistrationScreen = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const navigation = useNavigation();

  const handleRegister = async () => {
    const formData = new FormData();
    formData.append('username', username);
    formData.append('email', email);
    formData.append('password', password);
    formData.append('firstName', firstName);
    formData.append('lastName', lastName);

    // Log serialized data
    console.log('Serialized data to be sent:', formData);

    try {
      console.log('Attempting registration...');

      // Validate password and confirm password match
      if (password !== confirmPassword) {
        Alert.alert('Registration Failed', 'Passwords do not match.');
        return;
      }

      const response = await axios.post(
        'http://192.168.1.18/MyReactNative1/register.php',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      console.log('Server responded:', response.data);

      const responseData = response.data;

      if (responseData.success) {
        // Clear the form fields on successful registration
        setFirstName('');
        setLastName('');
        setUsername('');
        setEmail('');
        setPassword('');
        setConfirmPassword('');

        navigation.navigate('Login');
      } else {
        Alert.alert('Registration Failed', responseData.message);
      }
    } catch (error) {
      console.error('Registration failed:', error.message);
      Alert.alert('Registration Failed', 'Please try again later.');
    }
  };





  return (
    <View style={styles.container}>
      <Text style={styles.title}>Register</Text>

      <PaperTextInput
        label="First Name"
        value={firstName}
        onChangeText={(text) => setFirstName(text)}
        style={styles.input}
      />

      <PaperTextInput
        label="Last Name"
        value={lastName}
        onChangeText={(text) => setLastName(text)}
        style={styles.input}
      />

      <PaperTextInput
        label="Username"
        value={username}
        onChangeText={(text) => setUsername(text)}
        style={styles.input}
      />

      <PaperTextInput
        label="Email"
        value={email}
        onChangeText={(text) => setEmail(text)}
        keyboardType="email-address"
        style={styles.input}
      />

      <PaperTextInput
        label="Password"
        value={password}
        onChangeText={(text) => setPassword(text)}
        secureTextEntry
        style={styles.input}
      />

      <PaperTextInput
        label="Confirm Password"
        value={confirmPassword}
        onChangeText={(text) => setConfirmPassword(text)}
        secureTextEntry
        style={styles.input}
      />

      <Button mode="contained" style={styles.button} onPress={handleRegister}>
        Register
      </Button>

      <View style={styles.bottomContainer}>
        <Text>Already have an account?</Text>
        <TouchableOpacity onPress={() => navigation.navigate('Login')}>
          <Text style={styles.loginText}>Login</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 16,
  },
  title: {
    fontSize: 32,
    marginBottom: 20,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: 12,
  },
  button: {
    marginTop: 20,
  },
  bottomContainer: {
    marginTop: 20,
    alignItems: 'center',
  },
  loginText: {
    color: '#3498db',
    marginTop: 10,
  },
});

export default RegistrationScreen;
