import React, { useState } from 'react';
import { View, Text, Button, StyleSheet, TextInput, FlatList } from 'react-native';

const PointOfSale = () => {
  const [cart, setCart] = useState([
    { name: 'Item 1', price: 10, quantity: 0 },
    { name: 'Item 2', price: 15, quantity: 0 },
    { name: 'Item 3', price: 20, quantity: 0 },
  ]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [customerAmount, setCustomerAmount] = useState('');

  const incrementQuantity = (itemIndex) => {
    setCart((prevCart) =>
      prevCart.map((item, index) =>
        index === itemIndex ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  const decrementQuantity = (itemIndex) => {
    setCart((prevCart) =>
      prevCart.map((item, index) =>
        index === itemIndex && item.quantity > 0
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price * item.quantity;
    });
    setTotalPrice(total);
  };

  const handleCalculateTotal = () => {
    calculateTotal();
  };

  const handleCalculateChange = () => {
    const change = parseFloat(customerAmount) - totalPrice;
    alert(`Change: $${change.toFixed(2)}`);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Point of Sale</Text>
      <FlatList
        data={cart}
        keyExtractor={(item, index) => index.toString()}
        renderItem={({ item, index }) => (
          <View style={styles.itemContainer}>
            <Text>{item.name}</Text>
            <Text>Quantity: {item.quantity}</Text>
            <Text>Price: ${item.price}</Text>
            <View style={styles.quantityContainer}>
              <Button title="-" onPress={() => decrementQuantity(index)} />
              <Text style={styles.quantityText}>{item.quantity}</Text>
              <Button title="+" onPress={() => incrementQuantity(index)} />
            </View>
          </View>
        )}
      />
      <View style={styles.totalContainer}>
        <Text>Total Price: ${totalPrice.toFixed(2)}</Text>
        <TextInput
          style={styles.input}
          placeholder="Customer Amount"
          keyboardType="numeric"
          value={customerAmount}
          onChangeText={(text) => setCustomerAmount(text)}
        />
        <Button title="Calculate Change" onPress={handleCalculateChange} />
      </View>
      <Button title="Calculate Total" onPress={handleCalculateTotal} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'left',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  itemContainer: {
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    borderWidth: 1,
    padding: 10,
  },
  quantityContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  quantityText: {
    marginHorizontal: 10,
  },
  totalContainer: {
    marginTop: 20,
    alignItems: 'center',
  },
  input: {
    width: 200,
    borderBottomWidth: 1,
    marginBottom: 10,
  },
});

export default PointOfSale;
