import React, { useState } from 'react';
import { View, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { Text, TextInput as PaperTextInput, Button } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';

const RegistrationScreen = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigation = useNavigation();

  const handleRegister = async () => {
    try {
      console.log('Attempting registration...');

      const response = await fetch('http://192.168.1.18:3000/register.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username,
          email,
          password,
        }),
      });

      const responseData = await response.json();

      console.log('Server response:', responseData.message);

      if (responseData.success) {
        navigation.navigate('Login');
      } else {
        Alert.alert('Registration Failed', responseData.message);
      }
    } catch (error) {
      console.error('Registration failed:', error.message);
      Alert.alert('Registration Failed', 'Please try again later.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Register</Text>

      <PaperTextInput
        label="Username"
        value={username}
        onChangeText={(text) => setUsername(text)}
        style={styles.input}
      />

      <PaperTextInput
        label="Email"
        value={email}
        onChangeText={(text) => setEmail(text)}
        keyboardType="email-address"
        style={styles.input}
      />

      <PaperTextInput
        label="Password"
        value={password}
        onChangeText={(text) => setPassword(text)}
        secureTextEntry
        style={styles.input}
      />

      <Button mode="contained" style={styles.button} onPress={handleRegister}>
        Register
      </Button>

      <View style={styles.bottomContainer}>
        <Text>Already have an account?</Text>
        <TouchableOpacity onPress={() => navigation.navigate('Login')}>
          <Text style={styles.loginText}>Login</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 16,
  },
  title: {
    fontSize: 32,
    marginBottom: 20,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: 12,
  },
  button: {
    marginTop: 20,
  },
  bottomContainer: {
    marginTop: 20,
    alignItems: 'center',
  },
  loginText: {
    color: '#3498db',
    marginTop: 10,
  },
});

export default RegistrationScreen;
