import React, { useState } from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Modal,
  TextInput,
} from 'react-native';

const RestaurantPOS = () => {
  const [cart, setCart] = useState([]);
  const [items, setItems] = useState([
    { id: 1, name: 'Burger', price: 10.99 },
    { id: 2, name: 'Pizza', price: 15.99 },
    { id: 3, name: 'Salad', price: 8.99 },
    // Add more menu items here
  ]);


  // Call this function when your component mounts to fetch menu items
  useEffect(() => {
    fetchMenuItems();
  }, []);


  const [isModalVisible, setModalVisible] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState('');
  const [change, setChange] = useState(null);
  const [insufficientFundsModalVisible, setInsufficientFundsModalVisible] = useState(false);

  const addToCart = (itemId) => {
    const selectedItem = items.find((item) => item.id === itemId);
    if (selectedItem) {
      const updatedCart = [...cart, selectedItem];
      setCart(updatedCart);
    }
  };

  const removeFromCart = (itemId) => {
    const updatedCart = cart.filter((item) => item.id !== itemId);
    setCart(updatedCart);
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price;
    });
    return total.toFixed(2);
  };

  const handleCheckout = () => {
    // Check if the cart is empty
    if (cart.length === 0) {
      // Optionally, you can provide feedback to the user that the cart is empty.
      // For example, you can display a message or show an alert.
      alert('Your cart is empty. Please add items before checking out.');
      return; // Do not proceed with checkout if the cart is empty
    }

    // Show the payment modal
    setModalVisible(true);
  };

  const handlePayment = () => {
    // Handle the payment logic here
    // You can add validation and payment processing code
    const total = parseFloat(calculateTotal());
    const amountPaid = parseFloat(paymentAmount);

    if (amountPaid >= total) {
      // Calculate and set the change
      const changeAmount = (amountPaid - total).toFixed(2);
      setChange(changeAmount);
      // Close the payment modal
      setModalVisible(false);
    } else {
      // Insufficient funds
      setInsufficientFundsModalVisible(true);
    }
  };

  const handleInsufficientFundsModalOk = () => {
    // Close the insufficient funds modal and clear payment amount
    setInsufficientFundsModalVisible(false);
    setPaymentAmount('');
  };

  const handleUpdate = (itemId) => {
    // Add logic for updating the menu item
    alert(`Update item with ID ${itemId}`);
  };

  const handleDelete = (itemId) => {
    // Add logic for deleting the menu item
    alert(`Delete item with ID ${itemId}`);
  };

  return (
    <View style={styles.container}>
      <View style={styles.menu}>
        <Text style={styles.header}>Restaurant POS</Text>
        <ScrollView>
          {items.map((item) => (
            <View key={item.id} style={styles.menuItem}>
              <TouchableOpacity onPress={() => addToCart(item.id)}>
                <Text>{item.name}</Text>
                <Text>${item.price.toFixed(2)}</Text>
              </TouchableOpacity>
              <View style={styles.menuItemButtons}>
                <Button title="Update" onPress={() => handleUpdate(item.id)} />
                <Button title="Delete" onPress={() => handleDelete(item.id)} />
              </View>
            </View>
          ))}
        </ScrollView>
      </View>
      <View style={styles.cart}>
        <Text style={styles.header}>Cart</Text>
        <ScrollView>
          {cart.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.cartItem}
              onPress={() => removeFromCart(item.id)}
            >
              <Text>{item.name}</Text>
              <Text>${item.price.toFixed(2)}</Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
        <Text>Total: ${calculateTotal()}</Text>
        <Button title="Checkout" onPress={handleCheckout} />
      </View>
      <Modal visible={isModalVisible} transparent={true} animationType="slide">
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalHeader}>Payment</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter amount"
              keyboardType="numeric"
              onChangeText={(text) => setPaymentAmount(text)}
            />
            <Button title="Pay" onPress={handlePayment} />
            <Button
              title="Cancel"
              onPress={() => {
                setPaymentAmount('');
                setModalVisible(false);
              }}
            />
            {change !== null && (
              <Text>
                Change: ${change}
              </Text>
            )}
          </View>
        </View>
      </Modal>
      <Modal
        visible={insufficientFundsModalVisible}
        transparent={true}
        animationType="slide"
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalHeader}>Insufficient Funds</Text>
            <Text>Sorry, insufficient funds. Transaction cannot proceed.</Text>
            <Button title="OK" onPress={handleInsufficientFundsModalOk} />
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: '#f2f2f2',
  },
  menu: {
    flex: 1,
    padding: 20,
  },
  cart: {
    flex: 1,
    padding: 20,
    backgroundColor: 'white',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
    fontWeight: 'bold',
  },
  menuItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    width: '80%',
    padding: 20,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 5,
  },
  modalHeader: {
    fontSize: 20,
    marginBottom: 10,
    fontWeight: 'bold',
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },

  menuItemButtons: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 10,
  },
});

export default RestaurantPOS;
