import React, { useState } from 'react';
import { View, Text, Button, StyleSheet, TextInput, ScrollView } from 'react-native';

const PointOfSale = () => {
  const [cart, setCart] = useState([]);
  const [items, setItems] = useState([
    { name: 'Item 1', price: 10 },
    { name: 'Item 2', price: 15 },
    { name: 'Item 3', price: 20 },
  ]);

  const addToCart = (itemIndex, quantity) => {
    const newItem = { ...items[itemIndex], quantity };
    setCart([...cart, newItem]);
  };

  const incrementQuantity = (itemIndex) => {
    const updatedCart = [...cart];
    updatedCart[itemIndex].quantity += 1;
    setCart(updatedCart);
  };

  const decrementQuantity = (itemIndex) => {
    const updatedCart = [...cart];
    if (updatedCart[itemIndex].quantity > 0) {
      updatedCart[itemIndex].quantity -= 1;
      setCart(updatedCart);
    }
  };

  const updatePrice = (itemIndex, newPrice) => {
    const updatedItems = [...items];
    updatedItems[itemIndex].price = parseFloat(newPrice);
    setItems(updatedItems);
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price * item.quantity;
    });
    return total;
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Point of Sale</Text>
      <ScrollView style={styles.leftContainer}>
        {items.map((item, index) => (
          <View key={index} style={styles.itemRow}>
            <Text>{item.name}</Text>
            <TextInput
              style={styles.input}
              placeholder={`$${item.price}`}
              keyboardType="numeric"
              onChangeText={(text) => updatePrice(index, text)}
            />
            <Button title="+" onPress={() => incrementQuantity(index)} />
            <Text>{item.price}</Text>
          </View>
        ))}
      </ScrollView>
      <View style={styles.rightContainer}>
        {cart.map((item, index) => (
          <View key={index} style={styles.cartItem}>
            <Text>{item.name}</Text>
            <Button title="-" onPress={() => decrementQuantity(index)} />
            <Text>{item.quantity}</Text>
            <Button title="+" onPress={() => incrementQuantity(index)} />
            <Text>${item.price * item.quantity}</Text>
          </View>
        ))}
        <Text>Total Price: ${calculateTotal()}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
  },
  leftContainer: {
    flex: 1,
  },
  rightContainer: {
    flex: 1,
    alignItems: 'center',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  itemRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  cartItem: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  input: {
    width: 60,
    borderBottomWidth: 1,
    marginRight: 10,
  },
});

export default PointOfSale;
