import React, { useState } from 'react';
import { View, Text, Button, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';

const RestaurantPOS = () => {
  const [cart, setCart] = useState([]);
  const [items, setItems] = useState([
    { id: 1, name: 'Burger', price: 10.99 },
    { id: 2, name: 'Pizza', price: 15.99 },
    { id: 3, name: 'Salad', price: 8.99 },
    // Add more menu items here
  ]);

  const addToCart = (itemId) => {
    const selectedItem = items.find((item) => item.id === itemId);
    if (selectedItem) {
      const updatedCart = [...cart, selectedItem];
      setCart(updatedCart);
    }
  };

  const removeFromCart = (itemId) => {
    const updatedCart = cart.filter((item) => item.id !== itemId);
    setCart(updatedCart);
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price;
    });
    return total.toFixed(2);
  };

  return (
    <View style={styles.container}>
      <View style={styles.menu}>
        <Text style={styles.header}>Restaurant POS</Text>
        <ScrollView>
          {items.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.menuItem}
              onPress={() => addToCart(item.id)}
            >
              <Text>{item.name}</Text>
              <Text>${item.price.toFixed(2)}</Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>
      <View style={styles.cart}>
        <Text style={styles.header}>Cart</Text>
        <ScrollView>
          {cart.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.cartItem}
              onPress={() => removeFromCart(item.id)}
            >
              <Text>{item.name}</Text>
              <Text>${item.price.toFixed(2)}</Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
        <Text>Total: ${calculateTotal()}</Text>
        <Button title="Checkout" onPress={() => alert('Checkout button pressed')} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: '#f2f2f2',
  },
  menu: {
    flex: 1,
    padding: 20,
  },
  cart: {
    flex: 1,
    padding: 20,
    backgroundColor: 'white',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
    fontWeight: 'bold',
  },
  menuItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
});

export default RestaurantPOS;
