// Profile.js
import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image } from 'react-native';
import { useAuth } from './AuthContext'; // Adjust the path accordingly

const Profile = ({ navigation }) => {
  const { authenticatedUsername } = useAuth();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    if (authenticatedUsername) {
      fetch(`http://192.168.1.18/MyReactNative1/fetch.php?username=${authenticatedUsername}`)
        .then((response) => response.json())
        .then((data) => {
          setUserData(data);
        })
        .catch((error) => {
          console.error('Error fetching user data:', error);
        });
    }
  }, [authenticatedUsername]);

  return (
    <View style={styles.container}>
      {userData ? (
        <View style={styles.profileContainer}>
          <Image source={require('../image/profile.png')} style={styles.userImage} />
          <Text style={styles.userInfo}>Username: {userData.username}</Text>
          <Text style={styles.userInfo}>Full Name: {`${userData.first_name} ${userData.last_name}`}</Text>
          <Text style={styles.userInfo}>Email: {userData.email}</Text>
        </View>
      ) : (
        <Text>No user data available</Text>
      )}

      {/* Navigation footer */}
      <View style={styles.footer}>
        <TouchableOpacity onPress={() => navigation.navigate('Home')}>
          <Image source={require('../image/home.png')} style={styles.footerIcon} />
        </TouchableOpacity>
        <TouchableOpacity onPress={() => navigation.navigate('Cart')}>
          <Image source={require('../image/cart.png')} style={styles.footerIcon} />
        </TouchableOpacity>
        <TouchableOpacity onPress={() => navigation.navigate('OrderHistory')}>
          <Image source={require('../image/order_history.png')} style={styles.footerIcon} />
        </TouchableOpacity>
        <TouchableOpacity onPress={() => navigation.navigate('Profile')}>
          <Image source={require('../image/profile.png')} style={styles.footerIcon} />
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  profileContainer: {
    alignItems: 'center',
  },
  userImage: {
    width: 100,
    height: 100,
    borderRadius: 50,
    marginBottom: 16,
  },
  userInfo: {
    fontSize: 18,
    marginBottom: 8,
  },
  footer: {
    backgroundColor: '#ddd',
    padding: 10,
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
  },
  footerIcon: {
    width: 30,
    height: 30,
  },
});

export default Profile;
