import React, { useState } from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';

const PointOfSale = () => {
  const [quantities, setQuantities] = useState(Array(items.length).fill(0));
  const items = [
    { name: 'Item 1', price: 10 },
    { name: 'Item 2', price: 15 },
    { name: 'Item 3', price: 20 },
  ];

  const incrementQuantity = (itemIndex) => {
    const updatedQuantities = [...quantities];
    updatedQuantities[itemIndex] += 1;
    setQuantities(updatedQuantities);
  };

  const decrementQuantity = (itemIndex) => {
    if (quantities[itemIndex] > 0) {
      const updatedQuantities = [...quantities];
      updatedQuantities[itemIndex] -= 1;
      setQuantities(updatedQuantities);
    }
  };

  const calculateTotal = () => {
    let total = 0;
    items.forEach((item, index) => {
      total += item.price * quantities[index];
    });
    return total;
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Point of Sale</Text>
      {items.map((item, index) => (
        <View key={index} style={styles.itemContainer}>
          <Text>{item.name} - ${item.price}</Text>
          <View style={styles.quantityContainer}>
            <Button title="-" onPress={() => decrementQuantity(index)} />
            <Text>{quantities[index]}</Text>
            <Button title="+" onPress={() => incrementQuantity(index)} />
          </View>
        </View>
      ))}
      <Button title="Calculate Total" onPress={() => alert(`Total Price: $${calculateTotal()}`)} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  itemContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  quantityContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
});

export default PointOfSale;
