import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';

const ProductDetailScreen = ({ route }) => {
  const { product } = route.params;

  const handleAddToCart = () => {
    // Implement your logic for adding the product to the cart
    console.log('Product added to cart:', product.ProductName);
  };

  const handleCheckout = () => {
    // Implement your logic for the checkout process
    console.log('Checkout initiated for:', product.ProductName);
  };

  return (
    <View style={styles.container}>
      <View style={styles.productInfo}>
        <Text style={styles.productName}>{product.ProductName}</Text>
        <Text style={styles.productPrice}>{`$${product.Price}`}</Text>
        {/* Add more details as needed */}
      </View>

      {/* Add some space */}
      <View style={styles.space} />

      {/* Add to Cart button */}
      <TouchableOpacity
        style={[styles.button, { backgroundColor: 'blue' }]}
        onPress={handleAddToCart}
      >
        <Text style={styles.buttonText}>Add to Cart</Text>
      </TouchableOpacity>

      {/* Add some space */}
      <View style={styles.space} />

      {/* Checkout button */}
      <TouchableOpacity
        style={[styles.button, { backgroundColor: 'orange' }]}
        onPress={handleCheckout}
      >
        <Text style={styles.buttonText}>Checkout</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'flex-end',
  },
  productInfo: {
    marginBottom: 16,
  },
  productName: {
    fontSize: 24,
    fontWeight: 'bold',
    fontFamily: 'Cochin', // Replace with your preferred font
  },
  productPrice: {
    fontSize: 18,
    color: 'green',
    marginTop: 8,
  },
  button: {
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    fontFamily: 'Cochin', // Replace with your preferred font
  },
  space: {
    height: 16, // Adjust the height for the desired spacing
  },
});

export default ProductDetailScreen;
