import React, { useState } from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';

const PointOfSale = () => {
  const [items, setItems] = useState([
    { name: 'Item 1', price: 10, quantity: 0 },
    { name: 'Item 2', price: 15, quantity: 0 },
    { name: 'Item 3', price: 20, quantity: 0 },
  ]);

  const incrementQuantity = (itemIndex) => {
    const updatedItems = [...items];
    updatedItems[itemIndex].quantity += 1;
    setItems(updatedItems);
  };

  const decrementQuantity = (itemIndex) => {
    const updatedItems = [...items];
    if (updatedItems[itemIndex].quantity > 0) {
      updatedItems[itemIndex].quantity -= 1;
      setItems(updatedItems);
    }
  };

  const calculateTotal = () => {
    let total = 0;
    items.forEach((item) => {
      total += item.price * item.quantity;
    });
    return total;
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Point of Sale</Text>
      {items.map((item, index) => (
        <View key={index} style={styles.itemContainer}>
          <Text>{item.name} - ${item.price}</Text>
          <View style={styles.quantityContainer}>
            <Button title="-" onPress={() => decrementQuantity(index)} />
            <Text>{item.quantity}</Text>
            <Button title="+" onPress={() => incrementQuantity(index)} />
          </View>
        </View>
      ))}
      <Button title="Calculate Total" onPress={() => alert(`Total Price: $${calculateTotal()}`)} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
  },
  itemContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  quantityContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
});
