import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Modal,
  TextInput,
} from 'react-native';

const RestaurantPOS = () => {
  const [cart, setCart] = useState([]);
  const [items, setItems] = useState([]);


  const fetchData = async () => {
    try {
      const response = await fetch('http://127.0.0.1/'); // Replace with your actual PHP script URL
      const data = await response.json();
      console.log('Fetched Data:', data);
      setItems(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const addToCart = (itemId) => {
    const selectedItem = items.find((item) => item.id === itemId);
    if (selectedItem) {
      const updatedCart = [...cart, selectedItem];
      setCart(updatedCart);
    }
  };

  const removeFromCart = (itemId) => {
    const updatedCart = cart.filter((item) => item.id !== itemId);
    setCart(updatedCart);
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price;
    });
    return total.toFixed(2);
  };

  return (
    <View style={styles.container}>
      <View style={styles.menu}>
        <Text style={styles.header}>Restaurant POS</Text>
        <ScrollView>
          {items.map((item) => (
            <View key={item.id} style={styles.menuItem}>
              <TouchableOpacity onPress={() => addToCart(item.id)}>
                <Text>{item.name}</Text>
                <Text>${item.price.toFixed(2)}</Text>
              </TouchableOpacity>
            </View>
          ))}
        </ScrollView>
      </View>
      <View style={styles.cart}>
        <Text style={styles.header}>Cart</Text>
        <ScrollView>
          {cart.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.cartItem}
              onPress={() => removeFromCart(item.id)}
            >
              <Text>{item.name}</Text>
              <Text>${item.price.toFixed(2)}</Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
        <Text>Total: ${calculateTotal()}</Text>
        <Button title="Checkout" onPress={() => {/* Implement your checkout logic here */}} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: '#f2f2f2',
  },
  menu: {
    flex: 1,
    padding: 20,
  },
  cart: {
    flex: 1,
    padding: 20,
    backgroundColor: 'white',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
    fontWeight: 'bold',
  },
  menuItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
});

export default RestaurantPOS;
