import React, { useState } from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Modal,
  TextInput,
} from 'react-native';

const RestaurantPOS = () => {
  const [cart, setCart] = useState([]);
  const [items, setItems] = useState([
    { id: 1, name: 'Burger', price: 10.99 },
    { id: 2, name: 'Pizza', price: 15.99 },
    { id: 3, name: 'Salad', price: 8.99 },
    // Add more menu items here
  ]);

  const [isModalVisible, setModalVisible] = useState(false);
  const [isNewItemModalVisible, setNewItemModalVisible] = useState(false);
  const [newItemName, setNewItemName] = useState('');
  const [newItemPrice, setNewItemPrice] = useState('');
  const [paymentAmount, setPaymentAmount] = useState('');
  const [change, setChange] = useState(null);
  const [insufficientFundsModalVisible, setInsufficientFundsModalVisible] = useState(false);

  const addToCart = (itemId) => {
    const selectedItem = items.find((item) => item.id === itemId);
    if (selectedItem) {
      const updatedCart = [...cart, selectedItem];
      setCart(updatedCart);
    }
  };

  const removeFromCart = (itemId) => {
    const updatedCart = cart.filter((item) => item.id !== itemId);
    setCart(updatedCart);
  };

  const calculateTotal = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price;
    });
    return total.toFixed(2);
  };

  const handleCheckout = () => {
    // Show the payment modal
    setModalVisible(true);
  };

  const handlePayment = () => {
    // Handle the payment logic here
    // You can add validation and payment processing code
    const total = parseFloat(calculateTotal());
    const amountPaid = parseFloat(paymentAmount);

    if (amountPaid >= total) {
      // Calculate and set the change
      const changeAmount = (amountPaid - total).toFixed(2);
      setChange(changeAmount);
      // Close the payment modal
      setModalVisible(false);
    } else {
      // Insufficient funds
      setInsufficientFundsModalVisible(true);
    }
  };

  const handleInsufficientFundsModalOk = () => {
    // Close the insufficient funds modal and clear payment amount
    setInsufficientFundsModalVisible(false);
    setPaymentAmount('');
  };

  const handleNewItem = () => {
    setNewItemModalVisible(true);
  };

  const handleAddNewItem = () => {
    if (newItemName && newItemPrice) {
      const newItem = {
        id: items.length + 1,
        name: newItemName,
        price: parseFloat(newItemPrice),
      };
      setItems([...items, newItem]);
      setNewItemModalVisible(false);
      setNewItemName('');
      setNewItemPrice('');
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.menu}>
        <Text style={styles.header}>Restaurant POS</Text>
        <ScrollView>
          {items.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.menuItem}
              onPress={() => addToCart(item.id)}
            >
              <Text>{item.name}</Text>
              <Text>${item.price.toFixed(2)}</Text>
            </TouchableOpacity>
          ))}
          <TouchableOpacity style={styles.menuItem} onPress={handleNewItem}>
            <Text>Add New Item</Text>
          </TouchableOpacity>
        </ScrollView>
      </View>
      <View style={styles.cart}>
        <Text style={styles.header}>Cart</Text>
        <ScrollView>
          {cart.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.cartItem}
              onPress={() => removeFromCart(item.id)}
            >
              <Text>{item.name}</Text>
              <Text>${item.price.toFixed(2)}</Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
        <Text>Total: ${calculateTotal()}</Text>
        <Button title="Checkout" onPress={handleCheckout} />
      </View>
      <Modal visible={isModalVisible} transparent={true} animationType="slide">
        {/* ... (unchanged) */}
      </Modal>
      <Modal visible={insufficientFundsModalVisible} transparent={true} animationType="slide">
        {/* ... (unchanged) */}
      </Modal>
      <Modal visible={isNewItemModalVisible} transparent={true} animationType="slide">
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalHeader}>Add New Item</Text>
            <TextInput
              style={styles.input}
              placeholder="Item Name"
              onChangeText={(text) => setNewItemName(text)}
            />
            <TextInput
              style={styles.input}
              placeholder="Item Price"
              keyboardType="numeric"
              onChangeText={(text) => setNewItemPrice(text)}
            />
            <Button title="Add Item" onPress={handleAddNewItem} />
            <Button
              title="Cancel"
              onPress={() => {
                setNewItemName('');
                setNewItemPrice('');
                setNewItemModalVisible(false);
              }}
            />
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: '#f2f2f2',
  },
  menu: {
    flex: 1,
    padding: 20,
  },
  cart: {
    flex: 1,
    padding: 20,
    backgroundColor: 'white',
  },
  header: {
    fontSize: 24,
    marginBottom: 20,
    fontWeight: 'bold',
  },
  menuItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 2,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    width: '80%',
    padding: 20,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 5,
  },
  modalHeader: {
    fontSize: 20,
    marginBottom: 10,
    fontWeight: 'bold',
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
});

export default RestaurantPOS;
